function navegacionFija(){const e=document.querySelector(".header"),t=document.querySelector(".sobre-festival");document.addEventListener("scroll",(function(){t.getBoundingClientRect().bottom<.1?e.classList.add("fixed"):e.classList.remove("fixed")}))}function crearGaleria(){const e=document.querySelector(".galeria-imagenes");for(let t=1;t<=16;t++){const n=document.createElement("IMG");n.loading="lazy",n.width="300",n.height="200",n.src=`src/img/gallery/thumb/${t}.jpg`,n.alt="Imagen Galería",n.onclick=function(){mostrarImagen(t)},e.appendChild(n)}}function mostrarImagen(e){const t=document.createElement("IMG");t.src=`src/img/gallery/full/${e}.jpg`,t.alt="Imagen Galería";const n=document.createElement("DIV");n.classList.add("modal"),n.onclick=cerrarModal;const o=document.createElement("BUTTON");o.textContent="X",o.classList.add("btn-cerrar"),o.onclick=cerrarModal,n.appendChild(t),n.appendChild(o);const c=document.querySelector("body");c.classList.add("overflow-hidden"),c.appendChild(n)}function cerrarModal(){const e=document.querySelector(".modal");e.classList.add("fade-out"),setTimeout((()=>{e?.remove();document.querySelector("body").classList.remove("overflow-hidden")}),500)}function resaltarEnlace(){document.addEventListener("scroll",(function(){const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion-principal a");let n="";e.forEach((function(e){const t=e.offsetTop,o=e.offsetHeight;window.scrollY>=t-o/3&&(n=e.id)})),t.forEach((function(e){e.getAttribute("href")==="#"+n?e.classList.add("active"):e.classList.remove("active")}))}))}function scrollNav(){document.querySelectorAll(".navegacion-principal a").forEach((function(e){e.addEventListener("click",(function(e){e.preventDefault();const t=e.target.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth"})}))}))}document.addEventListener("DOMContentLoaded",(function(){navegacionFija(),crearGaleria(),resaltarEnlace(),scrollNav()}));